BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "users" (
	"session_id"	TEXT,
	"no_correct_answers"	INTEGER,
	"category"	TEXT
);
CREATE TABLE IF NOT EXISTS "list_answered_questions" (
	"session_id"	TEXT,
	"index_answered_question"	INTEGER
);
CREATE TABLE IF NOT EXISTS "questions" (
	"id"	INTEGER,
	"question"	TEXT,
	"answer1"	TEXT,
	"answer2"	TEXT,
	"answer3"	TEXT,
	"correct_answer"	TEXT,
	"category"	TEXT,
	"image"	TEXT
);
COMMIT;
